//背光切换等待te信号
void wait_lcd_light_te_high(void)
{
	uint64_t tmp_count= 0;
	uint16_t cycle_index = 0;
	if (task_start_flag == 0)
	{
		return; 
	}
	tmp_count = te_count;
	
	DRV_LCD_LOG_PRINTF("11wait_lcd_te_high %x-%x\r\n",te_count,tmp_count);
	while ((te_count <= tmp_count)&&(cycle_index < 600))
	{
		vTaskDelay(1);
		cycle_index++;
	}
}
//等待te信号
void wait_lcd_te_high(void)
{
	uint64_t tmp_count= 0;
	uint16_t cycle_index = 0;
	if (task_start_flag == 0)
	{
		return; 
	}
	tmp_count = te_count;
	
	DRV_LCD_LOG_PRINTF("11wait_lcd_te_high %x-%x\r\n",te_count,tmp_count);
	while ((te_count <= tmp_count)&&(cycle_index < 300))
	{
		vTaskDelay(1);
		cycle_index++;
	}
}

//判断是否有te信号
uint8_t whether_lcd_te_high(void)
{
	uint64_t tmp_count= 0;
	uint16_t cycle_index = 0;
	uint8_t ret = 0;
	tmp_count = te_count;
	
	while ((te_count <= tmp_count)&&(cycle_index < 200))
	{	
		vTaskDelay(1);
		cycle_index++;		
	}
	DRV_LCD_LOG_PRINTF("whether_lcd_te_high %d\r\n",cycle_index);
	if (te_count > tmp_count)
	{
		DRV_LCD_LOG_PRINTF("cd_te_is_high\r\n");
		ret = 1;
	}

	return ret;
}

