	//drv_lcd_enable();
	am_hal_gpio_out_bit_set(BSP_GPIO_LCD_LSA0);
	am_hal_iom_spi_write(BSP_LCD_IOM, BSP_LCD_CS,
							sData.words, 1, AM_HAL_IOM_RAW);
	
	//drv_lcd_disable();
	
