void drv_lcd_switch_hpm_to_lpm(void)
{
   if(mp_flag == 0)
   	{
		return;
    }
   
	if (light_enable == 0)
	{
		//drv_lcd_ctrl_write(0x38);   // //High Power Mode ON 
		#ifdef WATXCH_LCD_TRULY//12-1hz
		if (truly_lcd_flag)
		{
		drv_lcd_ctrl_write(0xB2);	//Frame rate								
		drv_lcd_data_write(0X00);	//hpm 16HZ
		drv_lcd_data_write(0X02);  //lpm 1HZ
		}
		else
		{
		drv_lcd_ctrl_write(0xc0);   //                       
		drv_lcd_data_write(0X86);
		drv_lcd_ctrl_write(0xD6);   //                     
		drv_lcd_data_write(0X01);  // 
		am_util_delay_ms(100);
	
		drv_lcd_ctrl_write(0x39);   // Low Power Mode ON
		am_util_delay_ms(50);
		}
		#else
		drv_lcd_ctrl_write(0xc0);   //                       
		drv_lcd_data_write(0X86);
		drv_lcd_ctrl_write(0xD6);   //                     
		drv_lcd_data_write(0X01);  // 
		am_util_delay_ms(100);
	
		drv_lcd_ctrl_write(0x39);   // Low Power Mode ON
		am_util_delay_ms(50);
		#endif
		mp_flag = 0;
	}

}

void drv_lcd_switch_lpm_to_hpm(void)
{ 
	if(mp_flag == 1)
   	{
		return;
    }
	if (light_enable == 0)
	{
		//drv_lcd_ctrl_write(0x39);   // Low Power Mode ON
		#ifdef WATXCH_LCD_TRULY//1-12hz
		if (truly_lcd_flag)
		{
		drv_lcd_ctrl_write(0xB2);	//Frame rate								
		drv_lcd_data_write(0X00);	//hpm 16HZ
		drv_lcd_data_write(0X05);  //lpm 8HZ		
		}
		else
		{
		drv_lcd_ctrl_write(0x38);   // //High Power Mode ON 
		am_util_delay_ms(300);
	
		drv_lcd_ctrl_write(0xc0);   //                       
		drv_lcd_data_write(0X4A);
		drv_lcd_ctrl_write(0xD6);   //                     
		drv_lcd_data_write(0X02);  //    
		
		am_util_delay_ms(100);
		}
		#else

		drv_lcd_ctrl_write(0x38);   // //High Power Mode ON 
		am_util_delay_ms(300);
	
		drv_lcd_ctrl_write(0xc0);   //                       
		drv_lcd_data_write(0X4A);
		drv_lcd_ctrl_write(0xD6);   //                     
		drv_lcd_data_write(0X02);  //    
		
		am_util_delay_ms(100);
	#endif
		mp_flag =1;
	}

}
