/**
  * @brief  Draw a triangle (三点以顺时针方向排列)
  * @param  y1:  起点所在行
  * @param  x1:  起点所在列
  * @param  y2:  中点所在行
  * @param  x2:  中点所在列
  * @param  y3:  终点所在行
  * @param  x3:  终点所在列
  * @param  pencolor: pen color.
  * @param  linethick: line thickness.
  * @param  fillenable: Fill Enable.
  * @retval
  */
void LCD_SetTriangle(uint8_t y1, uint8_t x1, uint8_t y2, uint8_t x2, uint8_t y3, uint8_t x3,
						uint8_t pencolor, uint8_t linethick, uint8_t fillenable)
{
    // Bounding rectangle
	uint8_t maxx = x1>x2?(x1>x3?x1:x3):(x2>x3?x2:x3);
	uint8_t minx = x1<x2?(x1<x3?x1:x3):(x2<x3?x2:x3);
	uint8_t maxy = y1>y2?(y1>y3?y1:y3):(y2>y3?y2:y3);
	uint8_t miny = y1<y2?(y1<y3?y1:y3):(y2<y3?y2:y3);

	if(fillenable)
	{
		// Scan through bounding rectangle
		for(int y = miny; y < maxy; y++)
		{
			for(int x = minx; x < maxx; x++)
			{
				// When all half-space functions positive, pixel is in triangle
				if(((x1 - x) * (y2 - y) - (y1 - y) * (x2 - x) >= 0) &&
					((x2 - x) * (y3 - y) - (y2 - y) * (x3 - x) >= 0) &&
					((x1 - x) * (y3 - y) - (y1 - y) * (x3 - x) <= 0))
				{
				    if(y < LCD_LINE_CNT_MAX && x <LCD_PIXEL_ONELINE_MAX )
                    {            
					    g_dispBuf[y][x] = pencolor;
                    }
				}
			}
		}
	}
	else
	{
		LCD_SetLine(y1, x1, y2, x2, pencolor, linethick);
		//LCD_SetLine(y2, x2, y3, x3, pencolor, linethick);
		LCD_SetLine(y3, x3, y1, x1, pencolor, linethick);
	}
}

