/**
  * @brief  Display a chinese using a bitmap font set.
  * @param  fontCode:  pointer to text string.
  * @param  destx:  Destination X coordinate (0-239).
  * @param  desty:  Destination Y coordinate (0-239).
  * @param  forecolor:   Foreground color.
  * @param  bckgrndcolor:   Background color.
  * @param  chinsesParams:  Chinese display parameters structure @ref LCD_ChineseStruct
  * @retval
  */
static void LCD_SetChinese(uint16_t fontCode, uint8_t destx, uint8_t desty, uint8_t forecolor, 
						   uint8_t bckgrndcolor, LCD_ChineseStruct *chineseParams)
{
	uint16_t i,j,n;
	uint32_t addr;
	uint8_t pxlen,width,height;
	uint16_t *p_index = NULL;
	uint8_t *p_font = NULL;
	
	if((destx > LCD_LINE_CNT_MAX) || (desty > LCD_PIXEL_ONELINE_MAX))
	{
		return;
	}

	p_index = (uint16_t *)chineseParams->pxindex;
	p_font = (uint8_t *)chineseParams->pxdata;
	
	//查找字符所在字库位置
	for( n = 0; n < chineseParams->num; n++ )
	{
		if( fontCode == p_index[n])
		{
			break;
		}
	}
	if(n == chineseParams->num)
	{
		//字库中没有此字符
		return;
	}
	
	//根据字符宽度计算所占字节长度
	pxlen = ceil(chineseParams->width/8.f);
	//字符在字库中起始偏移地址
	addr = n * (chineseParams->height * pxlen);
	
	height = chineseParams->height;
	if((LCD_LINE_CNT_MAX - destx) < height)
	{
		//防止溢出
		height = LCD_LINE_CNT_MAX - destx;
	}
	
	width = chineseParams->width;
	if((LCD_PIXEL_ONELINE_MAX - desty) < width)
	{
		//防止溢出
		width = LCD_PIXEL_ONELINE_MAX - desty;
	}
	
	for(i=0; i < height; i++)
	{
		for(j = 0; j < width; j++)
		{
			if(p_font[addr+pxlen*i+j/8] & (0x80>>(j%8)))
			{
				g_dispBuf[destx + i][desty + j] = forecolor;
			}
			else
			{
				if(bckgrndcolor != LCD_NONE)
				{
					g_dispBuf[destx + i][desty + j] = bckgrndcolor;
				}
			}
		}
	}
}

