/**
  * @brief  Display a chinese string using a bitmap font set.
  * @param  code:  pointer to text string.
  * @param  word:  tring display parameters structure @ref SetWord_t
  * @retval
  */
void LCD_SetString(char *code,SetWord_t *word)
{
	uint16_t x,y,i;
	uint16_t cnnum = 0;
	uint16_t temp,num,len;
	LCD_CharStruct enParams = {0};
	LCD_ChineseStruct cnParams = {0};
	
	if(code == NULL)
	{
		return;
	}
	
	switch(word->size)
	{
		case LCD_FONT_16_SIZE:
//			enParams = Font_Char_16;
//			cnParams = Font_CN_16;
//			break;
		case LCD_FONT_24_SIZE:
			
//			enParams = Font_Char_24;
//			cnParams = Font_CN_24;
//			break;
			LCD_DisplayGTString(code, word);
			return;
		case LCD_FONT_32_SIZE:
			enParams = Font_Char_32;
			cnParams = Font_CN_32;
			break;
		default:
			return;
	}
	
	num = strlen(code);
	//获取中文个数
	for(i = 0; i < num;)
	{
		if(code[i]&0x80)
		{
			i += 2;
			cnnum += 1;
		}
		else
		{
			i += 1;
		}
	}
	
	x = word->x_axis;
	if(word->y_axis == LCD_CENTER_JUSTIFIED)
	{
		//居中显示
		len = num * enParams.width + (num - cnnum - 1)*word->kerning;
		if(len >= LCD_PIXEL_ONELINE_MAX)
		{
			y = 0;
		}
		else
		{
			y = (LCD_PIXEL_ONELINE_MAX - len) / 2;
		}
	}
	else
	{
		y = word->y_axis;
	}
	
	for(i = 0; i < num; )
	{
		if(*code&0x80)
		{
			//中文字符
			temp = *code;
			code++;
			temp <<= 8;
			temp |= *code;
			code++;
			
			LCD_SetChinese(temp, x, y, word->forecolor, word->bckgrndcolor, &cnParams);
			y += (cnParams.width + word->kerning);
			
			i += 2;
		}
		else
		{
			//英文字符
			temp = (*code & 0xFF);
			code++;
			
			LCD_SetChar(temp, x, y, word->forecolor, word->bckgrndcolor, &enParams);
			y += (enParams.width + word->kerning);
			
			i++;
		}
	}

}

